import React, { useState } from 'react';
import axios from 'axios';
import Head from 'next/head';
import Menu from '../components/Menu';
import {
   Container,
   Jumbotron,
   Button
} from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';


import { fas } from '@fortawesome/free-solid-svg-icons';
library.add(fas);

const detalhes = () => {


   var url_string = window.location.href;
   var url = new URL(url_string);
   var pokemon = url.searchParams.get("nome");

   const [imagem, setImagem] = useState();

   const imagemp = async () => {
      var response = await axios.get('https://pokeapi.co/api/v2/pokemon/' + pokemon);
      // setImagem(response.data.sprites.front_default)


      return response.data.sprites.front_default

      // console.log(response.data.sprites.front_default)      
      //response.data.sprites.front_default;
      //<img source={{uri:imagemPokemon(p.url)}} height="100" width="80"/>
   };

   return (
      // <h1>{Mayra}</h1>
      <div>

         <img source={{ uri: imagemp() }} height="100" width="80" />
      </div>

   )

}

export default detalhes;
