          <Button color="primary">Learn More</Button>
        </p>
      </Jumbotron>

    </div>
    );

Home.getInitialProps = async() =>{

  var response = await axios.get('https://pokeapi.co/api/v2/pokemon/home');
console.log(response);
  return {response : response.data};
}
  export default Home;
